import React from 'react';
import Square from './Square.js';

function winner(board) {
  const a = [
    [0,1,2],
    [3,4,5],
    [6,7,8],
    [0,3,6],
    [1,4,7],
    [2,5,8],
    [0,4,8],
    [2,4,6]
  ];
  for (let i in a) {
    const [x,y,z] = i;
    if(board[x] == board[y] == board[z]){
      return board[x];
    }
  }
  return null;
}

class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill(''),
      status: 'X',
      winner: '',
    };
  }

  handleClick(i) {
    const squares = this.state.squares.slice();
    if (squares[i] == '') {
      squares[i] = this.state.status;
      this.setState({squares: squares});
      if (this.state.status == 'X'){
        this.setState({status:'O'});
      }
      else if (this.state.status == 'O'){
        this.setState({status:'X'});
      }
    }
  }

  renderSquare(i) {
    return (
      <Square
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  render() {
    const status = winner(this.state.squares) ? 'Winner is ' + winner(this.state.squares) :'Next player: ' + this.state.status;
    this.state.winner = winner(this.state.squares);
    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

export default Board;
